import React from "react";
import { connect } from "react-redux";
import { fetchProdById } from "../store/actions/ProductsActions";
import { fetchCart, addProductToCart } from "../store/actions/CartActions";
import  OrderContainer  from "./OrderContainer";
import { bindActionCreators } from "redux";

class ProductShow extends React.Component {


  componentDidMount() {
    this.props.fetchProdById(this.props.match.params.product_id)
  }
  render() {

    return !this.props.product ? (
      <div>hi</div>
    ) : (
      <section className='product-show'>
        <div className='col span-2-of-3'>
          <div>
            {this.props.product.images.split(",").map((image) => {
              return (
                <div key={image.id}>
                  <img onClick={this.props.addProductToCart} key={image.id} src={image} />
                </div>
              );
            })}
          </div>
        </div>
        <div className='span-1-of-3' id='product-text'>
          <p className='product-brand-show-title'>{this.props.brand.name}</p>

          <p className='product-show-subtitle'>{this.props.product.name}</p>

          <p className='product-show-subtitle'>
            {this.props.product.price_string}
          </p>
          <OrderContainer
            brand={this.props.brand}
            product={this.props.product}
            sizes={this.props.sizes}
          />
          <div className='product-show-description-wrapper'>
            <p>{this.props.product.description}</p>
          </div>
        </div>
      </section>
    );
  }
}

const mapStateToProps = (state, ownProps) => {
  console.log(state, ownProps);
  return {
    product: state.productState.product,
    brand: state.productState.brand,
    sizes: state.productState.sizes,
  };
};

 const mapDispatchToProps = (dispatch) => {
   return bindActionCreators({cart: addProductToCart,
  fetchProdById: fetchProdById}, dispatch)
 }

export default connect(mapStateToProps, mapDispatchToProps)(ProductShow);
