import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";

const categories = [
  "Underwear",
  "Pant",
  "Jumpsuit",
  "Jacket",
  "T-Shirt",
  "Button-Up",
  "Tee Shirt",
  "Hoodie",
  "Long Sleeve Tee Shirt",
  "Long Sleeve Button Up",
];
const sizes = [
  "XS",
  "S",
  "M",
  "L",
  "XL",
  "2X",
  "3X",
  "4X",
  "XXL",
  "XXXL",
  "0",
  "2",
  "4",
  "6",
  "8",
  "10",
  "12",
  "14",
];
const ProductNav = (props) => {

  // const clickHandler = (e) => {
  //   e.preventDefault();
  //   props.fetchProdByBrand(e.target.id)
  // }

  const categoryHandler = (e) => {
    e.preventDefault();
    props.initProducts(e.target.value)
  }

  // const sizeHandler = (e, size) => {
  //   e.preventDefault();
  //   props.fetchProdBySize(size);
  // }

  return (
    <div className='product-nav'>
      <div className='product-nav-centered' onClick={() => console.log(props)}>
        <div className='dropdown'>
          <li class='product-nav-title dropbtn'>BRAND</li>|
          <div className='dropdown-content'>
            {props.brands.map((brand) => {
              return (
                <li
                  key={brand.id}
                  onClick={(e) => category(e)}
                  id={brand.id}
                  value={brand.id}
                >
                  {brand.name}
                </li>
              );
            })}
          </div>
        </div>
        <div className='dropdown'>
          <li class='product-nav-title dropbtn'>CATEGORY</li>|
          <div className='dropdown-content'>
            {categories.map((category) => {
              return (
                <li value={category} onClick={(e) => categoryHandler(e)}>
                  {category}
                </li>
              );
            })}
          </div>
        </div>
        <div className=' dropdown'>
          <li class='product-nav-title dropbtn'>SIZE</li>|
          <div className='row dropdown-content'>
            {sizes.map((size) => {
              return <li value={size} onClick={(e) => sizeHandler(e)}>{size}</li>;
            })}
          </div>
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  console.log(state)
  return {
    products: state.product.select,
    brands: state.brand.brands,
  };
};

const mapDispatchToProps = (dispatch) => {
   return {
     initProducts: (arg) => dispatch(actions.initProducts(arg)),
      fetchProdByBrand: (brand_id) => dispatch(actions.fetchProdByBrand(brand_id)),
      fetchProdByCategory: (category) => dispatch(actions.fetchProdByCategory(category)),
      fetchProdBySize: (size) => dispatch(actions.fetchProdBySize(size)),
    }
};


export default connect(mapStateToProps, mapDispatchToProps)(ProductNav);
