import * as actionTypes from "../actions/actionTypes";
import { updateObject } from "../utility";

const initialState = {
  orders: [],
  error: null,
  loading: false,
  message: null
}

const startOrder = (state, action) => {
  console.log(state, action.payload)
  return updateObject(state, {
    loading: true,
    message: null,
    error: false
  })
}

const orderSuccess = (state, action) => {
  console.log(state)
  if(action.payload.length < 1 || action.payload === []) {
    return state
  } else {
    const updated = [];
    state.orders.map(order => {
      updated.push({...order})
    })
    action.payload.map(order => {
      updated.push({order})
    })
    console.log(action.payload, updated)
    return updateObject(state, {
      orders: [...updated],
      loading: false,
      error: false,
      message: null
    })
  }

}

const fetchOrderSuccess = (state, action) => {
  return action.payload === state.orders ?  state :
   updateObject(state,  {
    orders: [...action.payload],
    loading: false,
    error: false,
    message: null
  })
}

const orderFailed = (state, action) => {
  console.log(state, action.payload)
  return updateObject(state, {
    loading: false, error: false, message: "You must be a subscriber to place orders"
  })
}

const deleteOrder = (state, action) => {
  console.log(state, action.payload) 
  const updated = []
  state.orders.map(order => {
    if(order.id !== action.payload) {
      updated.push({...order})
    }
    console.log(action.payload, updated)
  })
  return updateObject(state,  {
    order: [...updated], error: false, message: null, loading: false
  })
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case actionTypes.START_ORDER:
      return startOrder(state, action);
    case actionTypes.DELETE_ORDER:
      return deleteOrder(state, action);
    case actionTypes.ORDER_FAILED:
      return orderFailed(state, action);
    case actionTypes.ORDER_SUCCESS:
      return orderSuccess(state, action);
    case actionTypes.FETCH_ORDER_SUCCESS:
      return fetchOrderSuccess(state, action);
    default:
      return state;
  }
}

export default reducer
