import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";
import api from "../store/apis/api";

class OrderContainer extends React.Component {

  componentDidMount() {
    this.props.fetchOrders()
  }

  fetchOrders = () => {
    let id = this.props.userId
    api.get(`/users/${id}/orders`).then(response => this.setState({orders: response.data}))
  }

  handleClick = (e) => {
    e.preventDefault();

  };

  renderOrders = () => {
    return this.props.orders.map(order => {
      return(
        <div className="order-card">
          <div className="row order-item" ><span>Order Date:</span> {order.order.order_date}</div>
          <div className="row order-item" ><span>Total: </span>{order.order.total_cost_string}</div>
        </div>
      )}
     ) }

  render() {
    return (
        <div className="row order-box">
        <div className="row section-order">
          <div className="order-title">Order History</div>
         <div> {this.renderOrders()}</div>
         { console.log(this.props.orders)}
          </div>
        </div>
    );
  }
}

const msp = (state) => {
  console.log(state);
  return {
    cart: state.cartState,
    orders: state.order.orders,
    userId: state.auth.userId,
  };
};

const mdp = (dispatch) => {
  return {
    fetchOrders: () => dispatch(actions.fetchOrders())
  }
}

export default connect(msp, mdp)(OrderContainer);
