import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";
import CartItem from "./CartItem";

class Cart extends React.Component {
  componentDidMount() {
    this.props.initCart();
  }

  clickHandler = (e) => {
    e.preventDefault();
    return this.props.cart_items < 1
      ? this.alertUser()
      : this.props.initOrder(this.props.cart_id);
  };

// how to make work?
  alertUser = () => {
    return <div>Must have at least one item in cart.</div>;
  };

  findProduct = (item) => {
    let product = this.props.products.filter(
      (product) => product.id === item.product_id
    );
    return product[0];
  };

  findBrand = (product) => {
    console.log("in find brand", product);
    let brand = [];
    if (product !== "undefined" && product !== undefined) {
      brand = this.props.brands.filter(
        (brand) => brand.id === product.brand_id
      );
    }
    return brand[0];
  };

  renderList = () => {
    return this.props.cart_items.map((item) => {
      let product = this.findProduct(item);
      let brand = this.findBrand(product);
      return (
        <CartItem
          cart_item={item}
          brand={brand}
          key={item.id}
          product={product}
        />
      );
    });
  };

  render() {
    return (
      <section>
        <div className='row'>
          {this.renderList()}
          <button
            onClick={(e) => this.clickHandler(e)}
            className='cart-item-title-button'
            id='size-button'
          >
            SUBMIT ORDER
          </button>
          {this.alertUser}
        </div>
      </section>
    );
  }
}

const mapStateToProps = (state) => {
  console.log(state);
  return {
    cart_id: state.cart.cart_id,
    member_id: state.cart.member_id,
    cart_items: state.cart.cart_items,
    products: state.product.select,
    brands: state.brand.select,
    loading: state.cart.loading,
    error: state.cart.error,
    orders: state.order.orders,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    initCart: () => dispatch(actions.initCart()),
    addToCart: (user_id, product_id, size) =>
      dispatch(actions.addProductToCart(user_id, product_id, size)),
    removeProductFromCart: (user_id, product_id, size) =>
      dispatch(actions.removeProductFromCart(user_id, product_id, size)),
    initOrder: () => dispatch(actions.initOrder()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Cart);
