import React from "react";
import { Link } from "react-router-dom";

class ProductCard extends React.Component {
  render() {
    console.log(this.props.brand.name);
    let img = this.props.product.images.split(",")[0];
    return (
      <li className='col span-1-of-3'>
        <div className='product-card'>
          <div className='col product-outer'>
            <div className='product-photo'>
              <img alt={this.props.product.name} src={img} />
            </div>
            <React.Fragment>
              <ul className='product-card-details'>
                <li className='product-title'>
                  <li>{this.props.brand.name}</li>
                </li>

                <Link
                  className='product-subtitle'
                  to={`/products/${this.props.product.id}`}
                >
                  <li className='product-subtitle'>
                    {this.props.product.name}
                  </li>
                </Link>

                <li className='product-subtitle'>
                  <li>{this.props.product.price_string}</li>
                </li>
              </ul>
            </React.Fragment>
          </div>
        </div>
      </li>
    );
  }
}

 const mapStateToProps = (state, ownProps) => {
   console.log(state, ownProps)
   return { brand: state.brandState.brands.filter(brand => brand.id === ownProps.brand.id),
  product: state.productState.productSelection.filter(prod => prod.id === ownProps.product.id) }
 }

 export default connect(mapStateToProps)(ProductCard)
