import React from "react";
import { Cart } from "./Cart";
import * as actions from './in'
import { connect } from "react-redux";

import { bindActionCreators } from "redux";

export const OrderContainer = (props) => {
  const handleClick = () => {
    console.log(props);
    fetchCart(1);
    console.log(props);
  };
  return (
    <React.Fragment>
      <div className='product-add-to-bag-wrapper'>
        <div className='product-add-to-bag-img-wrapper'>
          <img
            onClick={(e) => handleClick(e)}
            alt=''
            color={props.product.colorway}
            id={props.product.product_number}
            src={props.product.images.split(",")[0]}
          />

          <img
            onClick={(e) => handleClick(e)}
            alt=''
            color={props.product.colorway}
            id={props.product.product_number}
            src={props.product.images.split(",")[2]}
          />
        </div>
      </div>
      <div className='product-add-to-bag-wrapper'>
        {props.sizes.map((size) => {
          return (
            <div
              onClick={handleClick}
              className='size-button product-show-title'
            >
              {size.size}
            </div>
          );
        })}
      </div>
    </React.Fragment>
  );
};

const msp = (state, ownProps) => {
  return {
    user: state.users,
    cart: state.carts,
  };
};

const mdp = (dispatch) => {
  return {
    fetchCart: (user_id) => dispatch(fetchCart(user_id)),
    addProductToCart: (cart_id, product_id) =>
      dispatch(addProductToCart(cart_id, product_id)),
    fetchUser: (user_id) => dispatch(fetchCart(user_id)),
  };
};
export default connect(msp, mdp)(OrderContainer);
