import React from "react";
import { Cart } from "./Cart";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";

import { bindActionCreators } from "redux";

export const OrderContainer = (props) => {
  const handleClick = (e) => {
    e.preventDefault()
    console.log(props);
    props.addProductToCart();
    console.log(props);
  };
  return (
    <React.Fragment>
      {/* <div className='product-add-to-bag-wrapper'>
        <div className='product-add-to-bag-img-wrapper'>
          <img
            onClick={(e) => handleClick(e)}
            alt=''
            color={props.product.colorway}
            id={props.product.product_number}
            src={props.product.images.split(",")[0]}
          />

          <img
            onClick={(e) => handleClick(e)}
            alt=''
            color={props.product.colorway}
            id={props.product.product_number}
            src={props.product.images.split(",")[2]}
          />
        </div>
      </div> */}
      <div className='product-add-to-bag-wrapper'>
        {props.sizes.map((size) => {
          return (
            <div
              onClick={(e) => handleClick(e)}
              className='product-show-title'
              id="size-button"
            >
              {size}
            </div>
          );
        })}
      </div>
    </React.Fragment>
  );
};

const msp = (state) => {
  console.log(state)
  return {
    cart: state.cartState,
  };
};

const mdp = (dispatch) => {
  return {
    fetchCart: (user_id) => dispatch(actions.fetchCart(user_id)),
    addProductToCart: (cart_id, product_id) =>
      dispatch(actions.addProductToCart(cart_id, product_id)),
    fetchUser: (user_id) => dispatch(actions.fetchCart(user_id)),
  };
};
export default connect(msp, mdp)(OrderContainer);
