import React from 'react'
import {connect} from 'react-redux'
import * as actions from "../store/actions/index";
import SizeButton from "./UI/Button/SizeButton";

const CartItem = (props) => {
  const cart_item  = props.cart_items.filter(item => item.product_id === props.product.id)


  const clickHandler = (e) => {
    e.preventDefault();
    console.log("e.target.id",e.target.id, "props", props,"cartitem", cart_item[0])
    props.removeProductFromCart(cart_item[0])
  };

  return (
    <div>
      {!props.error && !props.loading ? (
        <div className='row section-cart'>
            <div className='col cart-item-card'>
<section className='col cart-buttons-container'>
              <SizeButton
                onClick={(e) => clickHandler(e)}
                className='plan-subtitle'
                id='size-button'
                value="delete"
              >
                REMOVE FROM CART
              </SizeButton>
              {/* <SizeButton
                clicked={(e) => clickHandler(e)}
                className='product-show-title'
                id='size-button'
                value="edit"
              >
                EDIT
              </SizeButton> */}
</section>
            <span className='cart-item-img-wrapper'>
              <img src={props.product.images.split(",")[0]} />
            </span>
              <ul className='cart-item-details-container'>
                <li>{props.brand.name}</li>
                <li>{props.product.name}</li>
                <li>{props.product.price_string}</li>
                <li>{props.cart_item.size_string}</li>
                <li>{props.cart_item.quantity}</li>
              </ul>
            </div>
        </div>
      ) : (
        <div>loading</div>
      )}
    </div>
  );
}

const mapStateToProps = (state, ownProps) => {
  console.log(state, ownProps);
  return {
    cart_id: state.cart.cart_id,
    member_id: state.cart.member_id,
    cart_items: state.cart.cart_items,
    brands: state.brand.select,
    loading: state.cart.loading,
    error: state.cart.error
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    addToFavorites: (user_id, product_id) =>
      dispatch(actions.createFavorite(user_id, product_id)),
    removeProductFromCart: (cart_item) =>
      dispatch(actions.removeProductFromCart(cart_item)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(CartItem)
