import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";
import api from "../store/apis/api";

import Favorite from "./Favorite";
import { times } from "lodash";

class Favorites extends React.Component {

  // componentDidMount() {
  //   this.props.initFavorites()
  //   this.props.initBrands()
  // }


  fetchFavorites = async () =>{
  console.log('in init')
  let user = this.props.userId
  dispatch(startFetchFavorites())
   await api
    .get(`/users/${user}/favorites`)
    .then((resp) => {
      dispatch(setFavorites(resp.data))
    })
    .catch((error) => {
      dispatch(fetchFavoritesFailed(error))
    });
};

  findBrand = (product) => {
    console.log(product)
    if(product !== 'undefined' && product !== undefined)
    {let brand = this.props.brands.filter(
      (brand) => brand.id === product.brand_id
    );
    console.log(this.props.brands, brand)
    return brand[0]}
  };

  renderList = () => {
    return this.props.favorites.map((fave) => {
      let product = fave['product']
      let brand = this.findBrand(product)
      console.log(fave['product'], brand)
      return (
        <Favorite
            brand={brand}
            fave={fave}
            product={product}
            size={fave.size}
          />
      );
     })
  };

  render() {
    return (
      <div>
        <div className='section-products'>{this.renderList()}</div>
      </div>
    );
  }
}


const mapStateToProps = (state, ownProps) => {
  console.log(state);
  return {
    favorites: state.favorite.select,
    brands: state.brand.select,
    products: state.product.select,
    userId: state.auth.userId,
    error: state.favorite.error,
    loading: state.favorite.loading
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    initFavorites: () => dispatch(actions.initFavorites()),
    initBrands: () => dispatch(actions.initBrands()),
    addToCart: (product_id) => dispatch(actions.addProductToCart(product_id)),
    addToFavorites: (user_id, product_id) => dispatch(actions.createFavorite(user_id, product_id)),
    deleteFavorite: (favorite) => dispatch(actions.deleteFavorite(favorite)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Favorites);
