import api from "../apis/api";

export const addProductToCart = (user_id, size_id) => async (dispatch) => {
  console.log('hi')
  const data = {user_id: user_id, size_id: size_id}
  const response = await api.patch(`/users/${user_id}/cart`, {data})
  dispatch({ type: "ADD_PRODUCT_TO_CART", payload: response.data });
};

export const fetchCart = (user_id) => async dispatch => {
  console.log('hi')
  const response = await api.get(`/users/${user_id}/cart`)
  dispatch({type: "FETCH_CART", payload: response.data});
}
