import React from 'react'
import CreateUserPlanForm from './createUserPlanForm'
import { connect, useDispatch } from "react-redux";
import * as actions from "../store/actions/index";
import PlanCard from './PlanCard'


class Plans extends React.Component {

  componentDidMount() {
    this.props.fetchPlans()
  }

  renderPlans = () => {
    console.log( this.props.plans)
    // this.props.plans.map(plan => { return (<PlanCard plans={plan}/>)})
  } 
  render() {
    let plans = this.renderPlans
    if (this.props.loading === true || this.props.loading === null) {
      plans = <div>loading</div>
    } else {
      plans()
    }
    return (
      <div>
      {plans}
         <div className='row'></div>
         {console.log(this.props)}
         <PlanCard />
         <div>

         </div>
       </div>
     );}
}


const mapStateToProps = (state) => {
  return {
    loading: state.plans.loading,
    error: state.plans.error,
    plans: state.plans.all
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    userPlanStart: (user_id, plan_id) =>
      dispatch(actions.userPlanStart(user_id, plan_id)),
    fetchPlans: () => dispatch(actions.fetchPlans()),
  };
};



export default connect(mapStateToProps, mapDispatchToProps)(Plans);
