import React from "react";
import { connect } from "react-redux";
import * as actions from "../store/actions/index";
import { Redirect } from "react-router-dom";
import Favorites from './Favorites'

const Profile = (props) => {

     const renderPlan = () => {
       let value
       if(props.current_user && props.current_user.subscribed) {
         value = <div>Swap Plans</div> 
       } else {
        value =  <div>Choose A Plan</div>; 
       }
       return value
     };

    let renderUser = null;
    if (props.current_user) {
      renderUser = <div>{props.current_user.name}</div>;
    } else if (!props.current_user) {
      renderUser = <div>no user found</div>;
    }


    return (
      <div>
        <div>{renderUser}</div>
        <div>{renderPlan()}</div>
        <div>
          <p className="title">Favorites</p>
          <Favorites />
        </div>
      </div>
    );

}
const mapStateToProps = (state) => {
  return { userId: state.auth.userId,
    current_user: state.auth.current_user,
    favorites: state.favorite.favorites,
    products: state.favorite.products,
    loading: state.auth.loading,
    cart: state.cart.cart,
    cart_items: state.cart.cart_items };
};


export default connect(mapStateToProps)(Profile);

// export default Profile
