import api from "../apis/api";
import * as actionTypes from "../actions/actionTypes";


const initialState = {
  cart: [],
  cart_items: [],
};

const fetchCart = (user_id) => async dispatch => {
    const response = await api.get(`/users/${user_id}/cart`)
  dispatch({type: actionTypes.FETCH_CART, payload: response.data});
}

const addProductToCart = (user_id, size_id)=> async (dispatch) => {
  console.log('hi')
  const data = {user_id: user_id, size_id: size_id}
  const response = await api.patch(`/users/${user_id}/cart`, {data})
  dispatch({ type: actionTypes.ADD_PRODUCT_TO_CART,  payload: response.data });
};


const reducer = (state = initialState, action) => {
  switch (action.type) {
    case actionTypes.FETCH_BRANDS:
      return setBrands(state, action);
    case actionTypes.FETCH_BRAND:
      return setBrand(state, action);
    default:
      return state;
  }
};

export default reducer;
