import React, {useState} from "react";
import { Link, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import * as actions from "../store/actions/index";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";

const PlanCard = (props) => {
  const [show, setShow] = useState()

  const alertMessage = ( ) => {
    console.log(props.error_message);
    let signUpResponse;
    if (props.error === true && props.error_message !== "undefined") {
      signUpResponse = 
       <>
      <Button variant="primary" onClick={() => setShow(true)}>
        Custom Width Modal
      </Button>

      <Modal
        show={show}
        onHide={() => setShow(false)}
        dialogClassName="modal-90w"
        aria-labelledby="example-custom-modal-styling-title"
      >
        <Modal.Header closeButton>
          <Modal.Title id="example-custom-modal-styling-title">
            Error
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>
            You are already subscribed to {props.current_user.plan.id}
          </p>
        </Modal.Body>
      </Modal>
    </>;
  } else {
     <>
      <Button variant="primary" onClick={() => setShow(true)}>
        Signup Successful!
      </Button>

      <Modal
        show={show}
        onHide={() => setShow(false)}
        dialogClassName="modal-90w"
        aria-labelledby="example-custom-modal-styling-title"
      >
        <Modal.Header closeButton>
          <Modal.Title id="example-custom-modal-styling-title">
            Custom Modal Styling
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>
            You are now subscribed to {props.plan.name}
          </p>
        </Modal.Body>
      </Modal>
    </>
  }

  const clickHandler = (e) => {
    e.preventDefault();
    props.initPlanMembership(props.plan.id).then(signUpHandler());
    console.log(props.current_user.plan_membership.plan_id, props.plan.id);
  };

  const signUpHandler = () => {
    console.log(props.error_message);
    let signUpResponse;
    if (props.error === true && props.error_message !== "undefined") {
      signUpResponse = <div>{props.error_message}</div>;
    } else {
      signUpResponse = <div>success</div>;
    }
    return signUpResponse;
  };

  const renderText = () => {
    return props.plan.id === props.current_user.plan_membership.plan_id
      ? "Current Plan"
      : "Choose Plan";
  };

  const renderId = () => {
    return props.plan.id === props.current_user.plan_membership.plan_id
      ? "current-plan"
      : "plan-option";
  };

  return (
    <div className='col span-1-of-3'>
      <div className='plan-card'>
        <div className='col plan-outer'></div>
        <React.Fragment>
          <div>{signUpHandler()}</div>
          <ul className='plan-card-details'>
            <li className='plan-title'>{props.plan.name}</li>
            <li>{props.plan.description}</li>
            <li className='plan-price'>{props.plan.price_string}/month</li>
          </ul>
        </React.Fragment>
      </div>
      <span>
        {props.token ? (
          <button
            id={renderId()}
            onClick={(e) => clickHandler(e)}
            value='TRY NOW'
          >
            {renderText()}
          </button>
        ) : (
          <Link to='/signup'>
            <button value='TRY NOW'>TRY NOW</button>
          </Link>
        )}
      </span>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    token: state.auth.token,
    current_user: state.auth.current_user,
    error: state.plan.error,
    error_message: state.plan.message,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    initPlanMembership: (plan_id) =>
      dispatch(actions.initPlanMembership(plan_id)),
  };
};
}
export default connect(mapStateToProps, mapDispatchToProps)(PlanCard);
