import * as actionTypes from "../actions/actionTypes";
import { updateObject } from "../utility";

export default (state = initialState, action) => {
  switch (action.type) {
    case "FETCH_BRAND":
      return state === "undefined" ? action.payload :
       [...state, action.payload]
      case "FETCH_BRANDS":
        return action.payload
    default:
      return state;
  }
};
