import * as actionTypes from "../actions/actionTypes";
import { updateObject } from "../utility";

export default (state = [], action) => {
  switch (action.type) {
    case "ADD_SIZE_TO_CART":
      return [...state, action.payload]
      default:
        return state
  }
}
