import api from "../apis/api";
import * as actionTypes from "../actions/actionTypes";

export const fetchCart = (user_id) => async dispatch => {
    const response = await api.get(`/users/${user_id}/cart`)
  dispatch({type: actionTypes.FETCH_CART, payload: response.data});
}

export const addProductToCart = (user_id, product_id, size_id)=> async (dispatch) => {
  console.log('hi')
  await api
    .patch(`/users/${user_id}/cart`, {
      params: {
        user_id: user_id,
        product_id: product_id,
        size_id: size_id,
      },
    })
    .then(resp =>
      dispatch({
        type: actionTypes.ADD_PRODUCT_TO_CART,
        payload: resp.data,
      })
    );
  ;
};
