import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";
import api from "../store/apis/api";

class OrderContainer extends React.Component {
  state = {
    orders: []
  }

  componentDidMount() {
    this.fetchOrders()
  }

  fetchOrders = () => {
    let id = this.props.userId
    api.get(`/users/${id}/orders`).then(response => this.setState({orders: response.data}))
  }

  handleClick = (e) => {
    e.preventDefault();

  };

  renderOrders = () => {
    this.state.orders.map(order => {
      return(
        <div>{order.id}</div>
      )}
     ) }

  render() {
    return (
      <div className="row">
        <div>See Past Orders</div>

       { console.log(this.props.orders)}
        </div>
    );
  }
}

const msp = (state) => {
  console.log(state);
  return {
    cart: state.cartState,
    orders: state.order.orders,
    userId: state.auth.userId,
  };
};

export default connect(msp)(OrderContainer);
