import React from "react";
import api from "../store/apis/api";
import { connect } from "react-redux";
import * as actions from "../store/actions/index";
import PlanCard from "./PlanCard";
import { Redirect } from "react-router";

class Plans extends React.Component {
  state = {
    plans: [],
  };

  componentDidMount() {
    this.fetchPlans();
  }

  fetchPlans = () => {
    api.get(`/plans`).then((resp) => {
      this.setState({ plans: resp.data });
    });
  };

  renderPlans = () => {
    return this.state.plans === null ? (
      <div>loading</div>
    ) : (
      this.state.plans.map((plan) => {
        return <PlanCard plan={plan} />;
      })
    );
  };

  clickHandler = (e) => {
    return e.target.value === 'delete-plan' ? this.props.userPlanDelete() : <Redirect to='/' />
  };
  renderHandler = () => {
    console.log(this.props);
    let signUpResponse;
    if (this.props.error_message !== "undefined" && this.props.error) {
      signUpResponse = (
        <div>
          <div className='title'>{this.props.error_message}</div>

          <button className='' value='delete-plan' onClick={(e) => this.clickHandler(e)}>
            Yes
          </button>

          <button className='' value='profile' onClick={(e) => this.clickHandler(e)}>
            No{" "}
          </button>
        </div>
      );
    } else {
      signUpResponse = <div>{this.renderPlans()}</div>;
    }
    return signUpResponse;
  };

  render() {
    return (
      <div className='row plan-box'>

          <div className='row title'><li className="plan-title">PLANS</li></div>
        <div className='row'>{this.renderHandler()}</div>
        <div className="more-info">
          {/* more info */}
        </div>
        </div>

    );
  }
}

const mapStateToProps = (state) => {
  console.log(state);
  return {
    token: state.auth.token,
    loading: state.plan.loading,
    current_user: state.auth.current_user,
    plan: state.plan.plan_membership_id,
    error: state.plan.error,
    error_message: state.plan.message,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    initPlanMembership: (plan_id) =>
      dispatch(actions.initPlanMembership(plan_id)),
    userPlanDelete: () => dispatch(actions.userPlanDelete()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Plans);
