import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";
import { OrderContainer } from "./OrderContainer";

class ProductShow extends React.Component {
  componentDidMount() {
    this.props.fetchProdById(this.props.match.params.product_id);
  }
  render() {
    console.log(this.props);
    return !this.props.product ? (
      <div>hi</div>
    ) : (
      <section className='product-show'>
        <div className='col span-2-of-3'>
          <div>
            <img src={this.props.product.images} />
            <div key={this.props.product.images.id}>
              <img
                onClick={this.props.addProductToCart}
                key={this.props.product.images.id}
                src={this.props.product.images}
              />
            </div>
            )
          </div>
        </div>
        <div className='span-1-of-3' id='product-text'>
          <p className='product-brand-show-title'>{this.props.brand.name}</p>

          <p className='product-show-subtitle'>{this.props.product.name}</p>

          <p className='product-show-subtitle'>
            {this.props.product.price_string}
          </p>
          <OrderContainer
            brand={this.props.brand}
            product={this.props.product}
            sizes={this.props.sizes}
          />
          <div className='product-show-description-wrapper'>
            <p>{this.props.product.description}</p>
          </div>
        </div>
      </section>
    );
  }
}

const mapStateToProps = (state) => {
  console.log(state);
  return {
    product: state.productState.selectedProduct,
    brand: state.productState.selectProdBrand,
    sizes: state.productState.selectProdSizes,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    addProductToCart: (product_id) =>
      dispatch(actions.addProductToCart(product_id)),
    fetchProdById: (product_id) => dispatch(actions.fetchProdById(product_id)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ProductShow);
