import _ from "lodash";
import * as actionTypes from "../actions/actionTypes";
import { updateObject } from "../utility";

const initialState = {
  select: [],
  show: [],
  images: [],
  error: false,
};

const setProducts = (state, action) => {
  console.log("in set products", state, action);
  return updateObject(state,  {
    select:  [...action.products] ,
  });
};

const setShowProduct = (state, action) => {
  console.log(action.showProduct);
   return updateObject(state, {
     show: {...action.payload},
     images: [...action.payload.images.split(",")]
   })
};


const setFaveProducts = (state, action) => {
  return updateObject(state, {
    select: action.payload,
  });
};

const fetchProductsFailed = (state, action) => {
  return updateObject(state, { error: true });
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case actionTypes.FETCH_PRODUCTS_BY_BRAND:
      return setProducts(state, action);
    case actionTypes.FETCH_PRODUCTS_BY_CATEGORY:
      return setProducts(state, action);
    case actionTypes.FETCH_PRODUCTS_BY_SIZE:
      return setProducts(state, action);
    case actionTypes.FETCH_PRODUCTS_BY_FILTER:
      return setProducts(state, action);
    case actionTypes.SET_PRODUCTS:
      return setProducts(state, action);
    case actionTypes.SET_SHOW_PRODUCT:
      return setShowProduct(state, action);
    case actionTypes.FETCH_PRODUCTS_FAILED:
      return fetchProductsFailed(state, action);
    case actionTypes.FETCH_FAVE_PROD:
      return setFaveProducts(state, action);
    default:
      return state;
  }
};

export default reducer;
