import api from "../apis/api";
import * as actionTypes from "./actionTypes";


export const userPlanStart = (state, action) => {
 return {
   type: actionTypes.USER_PLAN_START
 }
}

export const userPlanDelete = (state, action) => {
  return {
    type: actionTypes.USER_PLAN_DELETE 
  }
}

export const userPlanInit = (member_id, plan_id) => {
 return dispatch => {
  dispatch(userPlanStart());
  const planData = {
    member_id: member_id,
    plan_id: plan_id
  }

  api
  .post(`/plans/`, planData)
  .then(response => {
    console.log(response.data)
    dispatch(userPlanSuccess(response.data))
  })
  .catch(err => {
    dispatch(userPlanFail(err.response.data.error))
  })
 }
}

export const userPlanSuccess = (plan_id) => {
  return {
   type: USER_PLAN_SUCCESS, plan: plan_id
  }
}

export const userPlanFail = (error) => {
  return {
    type: actionTypes.USER_PLAN_FAIL, error: error
  }
}
