// }
export const userPlanStart = (plan_id) => {
  console.log('in plan start')
 return {
   type: actionTypes.USER_PLAN_START
 }
}

export const userPlanDelete = (member_id, plan_id) => {
  return {
    type: actionTypes.USER_PLAN_DELETE,
  };
};


export const initPlanMembership = (member_id, plan_id) => {
 return dispatch => {
  dispatch(userPlanStart());
  const planData = {
    member_id: member_id,
    plan_id: plan_id
  }

  api
  .post(`/plans/`, planData)
  .then(response => {
    console.log(response.data)
    dispatch(userPlanSuccess(response.data))
  })
  .catch(err => {
    dispatch(userPlanFail(err.response.data.error))
  })
 }
}

export const userPlanSuccess = (plan_id) => {
  return {
   type: actionTypes.USER_PLAN_SUCCESS, plan: plan_id
  }
}

export const userPlanFail = (error) => {
  return {
    type: actionTypes.USER_PLAN_FAIL, error: error
  }
}
