import React from "react";
import * as actions from "../store/actions/index";
import { connect, useDispatch } from "react-redux";
import { bindActionCreators } from "redux";

const categories = [
  "Underwear",
  "Pant",
  "Jumpsuit",
  "Jacket",
  "T-Shirt",
  "Button-Up",
  "Tee Shirt",
  "Hoodie",
  "Long Sleeve Tee Shirt",
  "Long Sleeve Button Up",
];
const sizes = [
  "XS",
  "S",
  "M",
  "L",
  "XL",
  "2X",
  "3X",
  "4X",
  "XXL",
  "XXXL",
  "0",
  "2",
  "4",
  "6",
  "8",
  "10",
  "12",
  "14",
];
export const ProductNav = (props) => {
  const dispatch = useDispatch();

  const clickHandler = (e) => {
    e.preventDefault();
    dispatch(actions.fetchProdByBrand(e.target.id));
  };

  const categoryHandler = (e, category) => {
    e.preventDefault();
    dispatch(actions.fetchProdByCategory(category));
  };

  const sizeHandler = (e, size) => {
    e.preventDefault();
    dispatch(actions.fetchProdBySize(size));
  };
  return (
    <div className='product-nav'>
      <div className='product-nav-centered' onClick={() => console.log("hi")}>
        <div className='dropdown'>
          <li class='product-nav-title dropbtn'>BRAND</li>|
          <div className='dropdown-content'>
            {props.brands.map((brand) => {
              return (
                <li
                  key={brand.id}
                  onClick={(e) => clickHandler(e)}
                  id={brand.id}
                >
                  {brand.name}
                </li>
              );
            })}
          </div>
        </div>
        <div className='dropdown'>
          <li class='product-nav-title dropbtn'>CATEGORY</li>|
          <div className='dropdown-content'>
            {categories.map((category) => {
              return (
                <li onClick={(e) => categoryHandler(e, category)}>
                  {category}
                </li>
              );
            })}
          </div>
        </div>
        <div className=' dropdown'>
          <li class='product-nav-title dropbtn'>SIZE</li>|
          <div className='row dropdown-content'>
            {sizes.map((size) => {
              return <li onClick={(e) => sizeHandler(e, size)}>{size}</li>;
            })}
          </div>
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    products: state.product.productSelection,
    brands: state.brand,
  };
};

const mapDispatchToProps = (dispatch) => {
  return bindActionCreators(
    {
      fetchProdByBrand: actions.fetchProdByBrand,
      fetchProdByCategory: actions.fetchProdByCategory,
      fetchProdBySize: actions.fetchProdBySize,
    },
    dispatch
  );
};

export default connect(mapStateToProps, mapDispatchToProps)(ProductNav);
