import api from "../apis/api";
import * as actionTypes from "./actionTypes";
import * as actions from "../actions/index";

import _ from "lodash";

export const initFavorites = (user_id) => async dispatch => {
  await api.get(`/users/${user_id}/favorites`)
  .then(resp => {
    dispatch(setFavorites(resp.data))
  })
  .catch(error => {
    dispatch(fetchFavoritesFailed())
  })
}

export const fetchFavoritesFailed = () => {
  return {
    type: actionTypes.FETCH_FAVORITES_FAILED
  }
}

export const setFavorites = (favorites) => async dispatch => {
  console.log(favorites)
  const products = []
  await favorites.map(fave => products.push(fave.product))
  console.log(products).then(dispatch(setFaveProductBrands(products)))
  dispatch({type: actionTypes.SET_FAVORITES, favorites: favorites, products: products})
}

export const deleteFavorite = (user_id, favorite) => async dispatch => {
  const response = await api.delete(`/users/${user_id}/favorites/${favorite.id}`).then(data => console.log(data))
  dispatch({type: actionTypes.DELETE_FAVORITE, payload: response})
};

export const createFavorite = (user_id, product_id) => async dispatch => {
  const response = await api.post(`/users/${user_id}/favorites`, {
    member_id: user_id,
    product_id: product_id
  })
  dispatch({type: actionTypes.CREATE_FAVORITE, payload: response.data})
};
