import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";
import CartItem from "./CartItem";

const Cart = (props) => {

  const clickHandler = (e) => {
    e.preventDefault();
    props.current_user === null
      ? props.history.push("/plans")
      : authorized();
  };

  const authorized = () => {
    !props.current_user.plan_membership_id
      ? props.history.push("/plans")
      : props.initOrder();
  };

  const findProduct = (item) => {
    let product;
    product = props.products.filter(
      (product) => product.id === item.product_id
    );
    console.log(item, props.products, item)
    return product !== undefined ? product[0] : null;
  };

  const findBrand = (product) => {
    let brand;
    if (product !== undefined && product !== null) {
      brand = props.brands.filter(
        (brand) => brand.id === product.brand_id
      );
      return brand[0];
    } else {
      return null;
    }
  };

  const renderList = () => {
    return props.cart_items === undefined ||
      props.cart_items.length === 0 ? (
      <>
        <p>There are no items in your cart</p>
        <p>{props.message}</p>
      </>
    ) : (
      props.cart_items.map((item) => {
        console.log(item)
        let product = findProduct(item.cart_item);
        let brand = findBrand(product);
        return (
          <CartItem
            cart_item={item}
            brand={brand}
            key={item.id}
            product={product}
          />
        );
      })
    );
  };

    let submitButton,
      plan_items,
      cart,
      cart_items_count = 0,
      guestMessage,
      memberCart;

    !props.current_user
      ? (cart = "Cart")
      : (cart = null);



    if(!props.cart_items){ (submitButton = null)
    submitButton = (
          <button
            id='add-to-favorites-button'
            onClick={(e) => clickHandler(e)}
          >
            Checkout
          </button>
        )
      (cart_items_count = 0)
      }
    props.cart_items.map(
          (item) => (cart_items_count += item.quantity)
        );

    // !props.cart_items && !props.cart_items
    //   ? (cart_items_count = 0)
    //   : props.cart_items.map(
    //       (item) => (cart_items_count += item.quantity)
    //     );

    props.current_plan && props.current_plan
      ? (plan_items = props.current_plan.items)
      : (plan_items = null);

    guestMessage = <div>{cart}</div>;
    props.current_user
      ? (memberCart = "Cart")
      : (memberCart = (
          <div>
            <div className='cart-message'>
              <p>Free Shipping for Members</p>
              <p>
                {"Become a Verses member for fast and free shipping. "}
                <a
                  className='guest-cart-link'
                  onClick={() => props.history.push("/login")}
                >
                  {" "}
                  Join Us
                </a>{" "}
                or
                <a
                  className='guest-cart-link'
                  onClick={() => props.history.push("/login")}
                >
                  {" "}
                  Log In
                </a>
              </p>
            </div>
            <div className='spacer' />
          </div>
        ));

    return (
      <div className='section-products'>
        <div className='spacer' />
        <div className='cart-container'>
          {memberCart}
          {guestMessage}
          {renderList()}
          <hr id='order-hr' />
        </div>

        <div className='order-summary-container'>
          <p>Summary</p>
          <div className='order-summary'>
            <div>
              <p id='order-key'>Subtotal</p>
              {props.updatedTotal ? props.updatedTotal : "$0.00"}
            </div>
            <div>
              <p id='order-key'>Estimated Shipping & Handling</p>
              {"$0.00"}
            </div>
            <div>
              <p id='order-key'>Estimated Tax</p>-
            </div>
            <hr id='order-hr' />
            <div>
              <p id='order-total'>Total</p>
              {props.cart_total ? props.cart_total : "$0.00"}
            </div>
            <hr id='order-hr' />
          </div>
          {submitButton}
          <div className='cart-quantity-message'>
            {cart_items_count <= plan_items ? (
              <p>
                You can add up to {plan_items - cart_items_count} more items.
              </p>
            ) : (
              <p>You must remove {cart_items_count - plan_items} item(s).</p>
            )}
          </div>
        </div>
      </div>
    );
  }

const mapStateToProps = (state) => {
  return {
    current_user: state.auth.current_user,
    current_plan: state.plan.current_plan_membership,
    cart_total: state.cart.cart_total,
    cart_items: state.cart.cart_items,
    message: state.cart.message,
    products: state.product.select,
    brands: state.brand.select,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    initFetchPlans: () => dispatch(actions.initFetchPlans()),
    initCart: () => dispatch(actions.initCart()),
    initOrder: () => dispatch(actions.initOrder()),
    fetchUser: () => dispatch(actions.fetchUser()),
    fetchUserPlan: () => dispatch(actions.fetchUserPlan()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Cart);
