import React from "react";
import api from "../store/apis/api";
import { connect } from "react-redux";
import * as actions from "../store/actions/index";
import PlanCard from "./PlanCard";

class Plans extends React.Component {
  componentDidMount() {
    this.props.initFetchPlans();
    this.renderText()
    this.renderId()
  }


  renderText = () => {
    if (
      !this.props.current_user.plan_membership &&
      !this.props.current_user.plan_membership
    )
      return "Choose Plan";
    return this.props.plan.id === this.props.current_user.plan_membership.plan_id &&
      this.props.plan.id === this.props.current_user.plan_membership.plan_id
      ? "Current Plan"
      : "Choose Plan";
  };

  renderId = () => {
    if (!this.props.current_user.plan_membership) return "plan-option";
    return this.props.plan.id === this.props.current_user.plan_membership.plan_id &&
      this.props.plan.id === this.props.current_user.plan_membership.plan_id
      ? "current-plan"
      : "plan-option";
  };


  renderPlans = () => {
    return this.props.loading ? (
      <div>loading</div>
    ) : (
      this.props.plans.map((plan) => {
        return <PlanCard renderText={this.renderText()} renderId={this.renderId()} plan={plan} />;
      })
    );
  };

  clickHandler = (e) => {
    return e.target.value === "delete-plan" ? (
      this.props.userPlanDelete()
    ) : ( null
    );
  };

  renderHandler = () => {
    console.log(this.props);
    let signUpResponse;
    if (this.props.error_message !== "undefined" && this.props.error) {
      signUpResponse = (
        <div className='row'>
          <div className='title'>{this.props.error_message}</div>
          <div className='title'>
            <button
              id='form-button'
              value='delete-plan'
              onClick={(e) => this.clickHandler(e)}
            >
              Yes
            </button>

            <button
              id='form-button'
              value='profile'
              onClick={(e) => this.clickHandler(e)}
            >
              No{" "}
            </button>
          </div>
        </div>
      );
    } else {
      signUpResponse = <div>{this.renderPlans()}</div>;
    }
    return signUpResponse;
  };

  render() {
    return (
      <div className='row plan-box'>
        <div className='row title'>
          <li className='plan-title'>SELECT A PLAN TO GET STARTED</li>
        </div>
        <div className='more-info'>
          <div className='row'>{this.renderHandler()}</div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  console.log(state);
  return {
    token: state.auth.token,
    loading: state.plan.loading,
    current_user: state.auth.current_user,
    current_plan: state.plan.current_plan,
    plans: state.plan.select,
    error: state.plan.error,
    error_message: state.plan.message,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    initPlanMembership: (plan_id) =>
    dispatch(actions.initPlanMembership(plan_id)),
    userPlanDelete: () => dispatch(actions.userPlanDelete()),
    initFetchPlans: () => dispatch(actions.initFetchPlans())
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Plans);
