import React from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { fetchProdById } from "../store/actions/ProductsActions";
import { fetchCart, addProductToCart } from "../store/actions/CartActions";
import OrderContainer from "./OrderContainer";

class ProductCard extends React.Component {

  componentDidMount() {
    this.props.fetchProdById(this.props.product.id)
  }
  render() {
    console.log(this.props.brand.name);
    let img = this.props.product.images.split(",")[1];
    return (
      <li className='col span-1-of-3'>
        <div className='product-card'>
          <div className='col product-outer'>
            <div className='product-photo'>
              <img alt={this.props.product.name} src={img} />
            </div>
            <React.Fragment>
              <ul className='product-card-details'>
                <li className='product-title'>
                  <li>{this.props.brand.name}</li>
                </li>

                <Link
                  className='product-subtitle'
                  to={`/products/${this.props.product.id}`}
                >
                  <li className='product-subtitle'>
                    {this.props.product.name}
                  </li>
                </Link>

                <li className='product-subtitle'>
                  <li>{this.props.product.price_string}</li>
                </li>
              </ul>
            </React.Fragment>
          </div>
        </div>
      </li>
    );
  }
}
const mapStateToProps = (state, ownProps) => {
  console.log(state.productState, ownProps);
  return {
    product: state.productState.selectedProduct,
    brand: state.productState.selectProdBrand,
    sizes: state.productState.selectProdSizes,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    cart: (product_id) => dispatch(addProductToCart(product_id)),
    fetchProdById: (product_id) => dispatch(fetchProdById(product_id)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ProductCart);
