import React from 'react'
import { Link, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import * as actions from "../store/actions/index";


class OrderForm extends React.Component {




  calculateOrder = () => {
    if(this.props.cart_items === 'undefined' || this.props.cart_items.length > 1){ return null}

    let total = 0;
    if(this.props.cart_items === [] || this.props.cart_items <=1 )null 
    total = this.props.cart_items.reduce((item) => {
      total += item.price;
    });
    console.log(total);
    }


  render() {
    return (
      <div id='span-3-of-9' id='order-form-container'>
        <div className='order-info'>
          {this.calculateOrder()}
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    cart_items: state.cart.cart_items
  }
}


export default connect(mapStateToProps)(OrderForm)
