import React from "react";
import api from "../store/apis/api";
import CreateUserPlanForm from "./createUserPlanForm";
import { connect } from "react-redux";
import * as actions from "../store/actions/index";
import PlanCard from "./PlanCard";
import Modal from 'react-bootstrap/Modal'

class Plans extends React.Component {
  state = {
    plans: []
  }
  componentDidMount() {
    this.fetchPlans();
  }

  fetchPlans = () => {
    api.get(`/plans`).then(resp => {
      this.setState({plans: resp.data})
    })
  }

  renderPlans = () => {
    console.log(this.props)
    return this.state.plans === null ? <div>loading</div> :
     this.state.plans.map((plan) => {
      return (<PlanCard plan={plan} />);
    });
  }
  render() {

    return (
      <div>
        <div>
          <h3 className='plans-title'>PLANS</h3>
        </div>
        {this.renderPlans()}
        <div className='row'>
          <>
            <Button variant='primary' onClick={handleShow}>
              Launch demo modal
            </Button>

            <Modal show={show} onHide={handleClose}>
              <Modal.Header closeButton>
                <Modal.Title>Modal heading</Modal.Title>
              </Modal.Header>
              <Modal.Body>
                Woohoo, you're reading this text in a modal!
              </Modal.Body>
              <Modal.Footer>
                <Button variant='secondary' onClick={handleClose}>
                  Close
                </Button>
                <Button variant='primary' onClick={handleClose}>
                  Save Changes
                </Button>
              </Modal.Footer>
            </Modal>
          </>
        </div>
        <div></div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  console.log(state)
  return {
    loading: state.plan.loading,
    error: state.plan.error,
  };
};

export default connect(mapStateToProps)(Plans);
