import _ from 'lodash'
import * as actionTypes from "../actions/actionTypes";
import { updateObject } from "../utility";

const initialState = {
  productSelection: [],
  selectedProduct: [],
  selectProdBrand: [],
  selectProdSizes: [],
};

const setProducts = (state, action) => {
  console.log('in set products', state, action.payload
  )
  const updatedProducts = action.payload
  return updateObject(state, {
    productSelection: updatedProducts,
  });
};

const setSelectedProduct = (state, action) => {
  console.log(action, state)

  return updateObject(state, {fave: action.payload});
};

const setFaveProducts = (state, action) => {

  return updateObject(state, {
    productSelection: action.payload
  })
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case actionTypes.FETCH_PRODUCTS_BY_BRAND:
      return setProducts(state, action);
    case actionTypes.FETCH_ALL_PRODUCTS:
      return setProducts(state, action);
    case actionTypes.FETCH_PRODUCTS_BY_CATEGORY:
      return setProducts(state, action);
    case actionTypes.FETCH_PRODUCTS_BY_SIZE:
      return setProducts(state, action);
    case actionTypes.FETCH_PROD_BY_ID:
      return setSelectedProduct(state, action);
    case actionTypes.FETCH_FAVE_PROD:
      return setFaveProducts(state, action);
    default:
      return state;
  }
};

export default reducer;
