import React from 'react'
import {connect} from 'react-redux'
import * as actions from "../store/actions/index";
import SizeButton from "./UI/Button/SizeButton";

const CartItem = (props) => {

   const clickHandler = (e) => {
     e.preventDefault();
     let product_id = props.product.id;

    //  console.log(favorite_id, product_id, user_id);
    //  e.target.value === "delete"
    //    ? props.removeProductFromCart(favorite_id)
    //    : props.addProductToCart(user_id, product_id);
   };

  //  const findCartItem = ()

  return (
    <div>
      {!props.error && !props.loading ? (
        <div className='row section-cart'>
          <div className='col cart-item-card'>
            <span className='cart-item-card-img-wrapper'>
              <img src={props.product.images.split(",")[0]} />
            </span>
            <div className='col section-cart'>
              <ul className='cart-item-details-container'>
                <li>{props.brand.name}</li>
                <li>{props.product.name}</li>
                <li>{props.product.price_string}</li>
              </ul>
            </div>
          </div>
          <SizeButton
            clicked={(e) => clickHandler(e)}
            className='product-show-title'
            id='size-button'
            value={size}
            selected={false}
          >
            {size}
          </SizeButton>
        </div>
      ) : (
        <div>loading</div>
      )}
    </div>
  );
}

const mapStateToProps = (state) => {
  console.log(state);
  return {
    cart_id: state.cart.cart_id,
    member_id: state.cart.member_id,
    cart_products: state.cart.cart_products,
    cart_items: state.cart.cart_items,
    brands: state.brand.select,
    loading: state.cart.loading,
    error: state.cart.error
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    addToFavorites: (user_id, product_id) =>
      dispatch(actions.createFavorite(user_id, product_id)),
    removeProductFromCart: (product_id) =>
      dispatch(actions.removeProductFromCart(product_id)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(CartItem)
