import React from "react";
import { connect } from "react-redux";
import * as actions from "../store/actions/index";

const PlanCard = (props) => {

  const clickHandler = (e) => {
    e.preventDefault();
    validityCheck(e)
  };

  const validityCheck = (e) => {
    //user is logged in and button is their plan 
    props.current_user && e.target.id === props.current_user.plan_membership.plan_id ?
    alert('choose another plan') : 
    props.current_user && e.target.id !== props.current_user.plan_membership.plan_id ?
    props.initPlanMembership(props.plan.id) : alert('would you like to change your plan')
    //user is logged in and the button value is not their plan

    //user isn't logged in
  }

  const renderText = () => {
    if (!props.current_user.plan_membership) return "Choose Plan";
    return props.plan.id === props.current_user.plan_membership.plan_id &&
      props.plan.id === props.current_user.plan_membership.plan_id
      ? "Current Plan"
      : "Choose Plan";
  };

  const renderId = () => {
    if (!props.current_user.plan_membership) return "plan-option";
    return props.plan.id === props.current_user.plan_membership.plan_id &&
      props.plan.id === props.current_user.plan_membership.plan_id
      ? "current-plan"
      : "plan-option";
  };

  return (
        <div className='col span-1-of-3'>
      <div className='plan-card'>
        <div className='col plan-outer'>
          {/* {showErrors()} */}
          <React.Fragment>
            <ul className='plan-card-details'>
              <li className='plan-title'>{props.plan.name}</li>
              <li className='plan-price'>
                {props.plan.price_string}
                <span>/month</span>
              </li>
              <li className='plan-subtitle'>{props.plan.description}</li>
            </ul>
          </React.Fragment>
        </div>
        <hr />
        <div className='plan-card-details'>
          <ul className='plan-bullet-points'>
            <li>Rent 4 items at a time</li>
            <li>5,000+ items from 50+ designers</li>
            <li>Wear items with retail value up to $350</li>
            <li>Monthly shipments worth up to $1,400</li>
          </ul>
        </div>
        <div id='plan-button'>
          {props.current_user ? (
            <button
            validityCheck={validityCheck}
              plan={props.plan.id}
              id={renderId()}
              value='button'
              onClick={(e) => clickHandler(e)}
            >
              {renderText()}
            </button>
          ) : (
            <button
            validityCheck={validityCheck}
              plan={props.plan.id}
              id={renderId()}
              value='button'
              onClick={(e) => clickHandler(e)}
            >
              TRY NOW
            </button>
          )}
        </div>
      </div>
    </div>
  );
};
const mapStateToProps = (state) => {
  return {
    token: state.auth.token,
    current_user: state.auth.current_user,
    current_plan: state.plan.current_plan,
    error: state.plan.error,
    error_message: state.plan.message,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    initPlanMembership: (plan_id) =>
      dispatch(actions.initPlanMembership(plan_id)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(PlanCard);
