import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";
import ProductCard from "./ProductCard";
import { Link } from "react-router-dom";

class Favorite extends React.Component {
  // componentDidMount() {
  //   this.props.fetchProdById(this.props.fave.product_id);
  // }

  // console.log(this.props);
  render() {
    return (
        <li>
          <ProductCard
            brand={this.props.brand}
            key={this.props.key}
            product={this.props.product}
          />
              <li id="center" className='product-subtitle'>Delete Favorite</li>
              <li id="center" className='product-subtitle'>See Product</li>
        </li>
    );
  }
}

const mapStateToProps = (state, ownProps) => {
  console.log(state.productState, ownProps);
  return {
    showProduct: state.product.show,
    brand: state.brand.brand,
    sizes: state.size.sizes,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    cart: (product_id) => dispatch(actions.addProductToCart(product_id)),
    setShowProduct: (product_id) =>
      dispatch(actions.setShowProduct(product_id)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Favorite);
