import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";
import ProductCard from "./ProductCard";

class Cart extends React.Component {
  componentDidMount() {
    this.props.fetchCart(this.props.userId);
  }

  findBrand = (product) => {
    return this.props.cart_products.map(prod => {
      let brand = this.props.brands.filter(brand => brand.id === product.brand_id)
    })
  }

  renderList = () => {
    return this.props.cart_products.map(prod => {
      let brand = this.findBrand(prod)
      return <ProductCard brand={brand} key={prod.id} product={prod} />;
    })
  }

  render() {
    console.log(this.props)
    return (
      <React.Fragment>
        <div>
          {this.props.cart_products ? (
            this.props.cart_products.map((item) => {
              return <div>{item.product.name}</div>;
            })
          ) : (
            <div>hi</div>
          )}
        </div>
      </React.Fragment>
    );
  }
}

const mapStateToProps = (state) => {
  console.log(state);
  return {
    cart_id: state.cart.cart_id,
    member_id: state.cart.member_id,
    cart_items: state.cart.cart_items,
    cart_products: state.cart.cart_products,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    fetchCart: (user_id) => dispatch(actions.fetchCart(user_id)),
    addToCart: (product_id) => dispatch(actions.addProductToCart(product_id)),
    removeProductFromCart: (user_id, product_id, size) =>
      dispatch(actions.removeProductFromCart(user_id, product_id, size)),
    // removeProductFromCart: (user_id, product_id, size) => dispatch(actions.removeProductFromCart(user_id, product_id, size))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Cart);
