import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";
import { Link } from "react-router-dom";

class OrderContainer extends React.Component {
  componentDidMount() {
    // this.props.member_id
    this.props.fetchOrders();
    // : this.renderError();
  }

  componentDidUpdate() {
    // this.props.member_id
    this.props.fetchOrders();
    // : this.renderError();
  }
  handleClick = (e) => {
    e.preventDefault();
  };

  renderError = () => {
    console.log("in render order");
    return (
      <div>
        <div className='title-placeholder'>
          You must be a subscriber to place orders
        </div>
        <Link to={"/plans/"}>
          <button id='plan-option' value='button'>
            See Subscriptions
          </button>
        </Link>
      </div>
    );
  };

  renderOrders = () => {
    if (this.props.orders && this.props.orders) {
      return this.props.orders.map((order) => {
        return (
          <div className='order-card'>
            <div className='row order-item'>
              <span>Order Date:</span> {order.order_date}
            </div>
            <div className='row order-item'>
              <span>Total: </span>
              {order.total_cost_string}
            </div>
          </div>
        );
      });
    }
  };

  render() {
    return (
      <div className='row order-box'>
        <div className='row section-order'>
          <div className='order-title'>Order History</div>
          <div> {this.renderOrders()}</div>
          {/* <div className='order-title'> {this.renderError()}</div> */}
          {console.log(this.props.orders)}
        </div>
      </div>
    );
  }
}

const msp = (state) => {
  console.log(state);
  return {
    cart: state.cartState,
    orders: state.order.orders,
    error: state.order.error,
    userId: state.auth.userId,
    current_user: state.auth.current_user,
    member_id: state.auth.member_id,
  };
};

const mdp = (dispatch) => {
  return {
    fetchOrders: () => dispatch(actions.fetchOrders()),
  };
};

export default connect(msp, mdp)(OrderContainer);
