import React from "react";
import api from "../store/apis/api";
import { Link, } from "react-router-dom";
import { connect } from "react-redux";
import * as actions from "../store/actions/index";
import PlanCard from "./PlanCard";

class Plans extends React.Component {
  state = {
    plans: [],
  };

  componentDidMount() {
    this.fetchPlans();
  }

  fetchPlans = () => {
    api.get(`/plans`).then((resp) => {
      this.setState({ plans: resp.data });
    });
  };

  renderPlans = () => {
    console.log(this.props);
    return this.state.plans === null ? (
      <div>loading</div>
    ) : (
      this.state.plans.map((plan) => {
        return <PlanCard plan={plan} />;
      })
    );
  };

  renderText = () => {
    return this.props.plan.id ===
      this.props.current_user.plan_membership.plan_id
      ? "Current Plan"
      : "Choose Plan";
  };

  clickHandler = (e) => {
    e.preventDefault();
    this.props.userPlanDelete();
    console.log(
      this.props.current_user.plan_membership.plan_id,
      this.props.plan.id
    );
  };
  signUpHandler = () => {
    console.log(this.props.error_message);
    let signUpResponse;
    if (this.props.error === true && this.props.error_message !== "undefined") {
      signUpResponse = (
        <div>
          <div>{this.props.error_message}</div>
          <span>
            {this.props.token ? (
              <button onClick={(e) => this.clickHandler(e)} value='TRY NOW'>
                {this.renderText()}
              </button>
            ) : (
              <Link to='/signup'>
                <button value='TRY NOW'>TRY NOW</button>
              </Link>
            )}
          </span>
        </div>
      );
    } else {
      signUpResponse = <div>{this.renderPlans()}</div>;
    }
    return signUpResponse;
  };

  render() {
    return (
      <div>
        <div>
          <div className='plans-title'>PLANS</div>
        </div>
        <div className='row'>{this.signUpHandler()}</div>
        <div></div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  console.log(state)
  return {
    loading: state.plan.loading,
    token: state.auth.token,
    error: state.plan.error,
    error_message: state.plan.message,
    current_user: state.auth.current_user,
    plan: state.plan
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    initPlanMembership: (plan_id) =>
      dispatch(actions.initPlanMembership(plan_id)),
      userPlanDelete: () => dispatch(actions.userPlanDelete())
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Plans);
