import React from "react";
import api from "../store/apis/api";
import CreateUserPlanForm from "./createUserPlanForm";
import { connect } from "react-redux";
import * as actions from "../store/actions/index";
import PlanCard from "./PlanCard";

class Plans extends React.Component {
  state = {
    plans: []
  }
  componentDidMount() {
    this.fetchPlans();
  }

  fetchPlans = () => {
    api.get(`/plans`).then(resp => {
      this.setState({plans: resp.data})
    })
  }

  renderPlans = () => {
    console.log(this.props)
    return this.state.plans === null ? <div>loading</div> :
     this.state.plans.map((plan) => {
      return (<PlanCard plan={plan} />);
    });
  }
  render() {

    return (
      <div>
        <div>
          <h3 className='plan-title'>PLANS</h3>
        </div>
        {this.renderPlans()}
        <div className='row'></div>
        <div></div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  console.log(state)
  return {
    loading: state.plans.loading,
    error: state.plans.error,
    plans: state.plans.all,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    userPlanStart: (user_id, plan_id) =>
      dispatch(actions.userPlanStart(user_id, plan_id)),
    fetchPlans: () => dispatch(actions.fetchPlans()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Plans);
