import React from "react";
import { connect } from "react-redux";
import Aux from './Aux'


class Layout extends React.Component {


   render() {
     return ( 

           <Aux><main>{this.props.children}</main></Aux>

      )
   }
}



const mapStateToProps = (state) => {
  return {
    products: state.product.select,
    brands: state.brand.select,
    isAuthenticated: state.auth.token !== null,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    initProducts: () => dispatch(actions.initProducts()),
    initBrands: () => dispatch(actions.initBrands()),

    // fetchShowProductSizes: (brand_id, product_id) => dispatch(actions.fetchShowProductSizes(brand_id, product_id))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Layout);
