import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";
import ProductCard from "./ProductCard";
import { Link } from "react-router-dom";

class Favorite extends React.Component {
  // componentDidMount() {
  //   this.props.fetchProdById(this.props.fave.product_id);
  // }

  // console.log(this.props);
  render() {
    console.log(this.props)
    return (
        <li>
          <ProductCard
            brand={this.props.brand}
            key={this.props.key}
            product={this.props.product}
          />
              <p className='product-subtitle'>Delete Favorite</p>
              <p className='product-subtitle'>See Product</p>
        </li>
    );
  }
}

const mapStateToProps = state => {
  return {
    favorites: state.favorite.select
  }
}


const mapDispatchToProps = (dispatch) => {
  return {
    addToCart: (product_id) => dispatch(actions.addProductToCart(product_id)),
    addToFavorites: (user_id, product_id) => dispatch(actions.createFavorite(user_id, product_id)),
    deleteFavorite: (user_id, favorite) => dispatch(actions.deleteFavorite(user_id, favorite)),
    setShowProduct: (product_id) =>
      dispatch(actions.setShowProduct(product_id)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Favorite);
