import React from 'react'
import CreateUserPlanForm from './createUserPlanForm'
import { connect, useDispatch } from "react-redux";
import * as actions from "../store/actions/index";
import PlanCard from './PlanCard'


class Plans extends React.Component {

  componentDidMount() {
    this.props.fetchPlans()
  }
  clickHandler = (e) => {
    e.preventDefault()
    return this.props.loading === true ? this.renderPlans() : <li>loading</li> 
  }

  renderPlans = () => {
    let plans = this.props.plans
    return plans.map(plan => { return (<PlanCard plans={plan}/>)})
  } 
   render() {
     return (
       <div>
         <div className='row'>{this.renderPlans}</div>
         <CreateUserPlanForm />
         {console.log(this.props)}
         <PlanCard />
         <div>

         </div>
       </div>
     );}
}


const mapStateToProps = (state) => {
  return {
    loading: state.plans.loading,
    error: state.plans.error,
    plans: state.plans.all
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    userPlanStart: (user_id, plan_id) =>
      dispatch(actions.userPlanStart(user_id, plan_id)),
    fetchPlans: () => dispatch(actions.fetchPlans()),
  };
};



export default connect(mapStateToProps, mapDispatchToProps)(Plans);
